import React, { useState, useEffect } from 'react';
import { useTheme } from '../../context/ThemeContext';
import HeatmapDisplay from './HeatmapDisplay';
import styled from 'styled-components';
import { blackScholesApi } from '../../services/api';
import InputForm from './InputForm';
import ResultsDisplay from './ResultsDisplay';

const Container = styled.div`
  display: grid;
  grid-template-columns: 300px 1fr;
  min-height: calc(100vh - 60px);
  max-width: 1600px;
  margin: 0 auto;
  gap: 2rem;
  padding: 2rem;
  background-color: ${props => props.theme.mode === 'dark' ? '#1a1a1a' : '#fff'};
  color: ${props => props.theme.mode === 'dark' ? '#e0e0e0' : '#333'};
`;

const Sidebar = styled.div`
  background-color: ${props => props.theme.mode === 'dark' ? '#2d2d2d' : '#f8f9fa'};
  padding: 1.5rem;
  border-radius: 8px;
  box-shadow: 0 2px 10px rgba(0, 0, 0, ${props => props.theme.mode === 'dark' ? '0.5' : '0.1'});
  height: fit-content;
  border: 1px solid ${props => props.theme.mode === 'dark' ? '#404040' : 'transparent'};
`;

const MainContent = styled.div`
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const Title = styled.h1`
  margin-bottom: 1.5rem;
  color: ${props => props.theme.mode === 'dark' ? '#fff' : '#333'};
  font-size: 1.5rem;
`;

const ResultsGrid = styled.div`
  width: 100%;
`;

const BlackScholesCalculator = () => {
  // Default input values
  const defaultInputs = {
    stock_price: 100,
    strike_price: 100,
    time_to_expiry: 30,
    risk_free_rate: 0.05,
    volatility: 0.2,
    time_in_days: true,
    ticker: ''
  };

  // State management
  const [inputs, setInputs] = useState(defaultInputs);
  const [results, setResults] = useState(null);
  const [greeks, setGreeks] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [heatmapData, setHeatmapData] = useState(null);

  // Calculate option price when inputs change
  useEffect(() => {
    calculateOptionPrice();
  }, [inputs]);

  // Calculate option price
  const calculateOptionPrice = async () => {
    try {
      setIsLoading(true);

      // Calculate both call and put prices and Greeks in one API call
      const response = await blackScholesApi.calculateOptions(inputs);

      setResults({
        call_price: response.call_price,
        put_price: response.put_price
      });

      setGreeks({
        call: response.call_greeks,
        put: response.put_greeks
      });
    } catch (error) {
      console.error('Error in Black-Scholes calculation:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle input changes
  const handleInputChange = (name, value) => {
    setInputs({
      ...inputs,
      [name]: value
    });
  };



  // Reset inputs to defaults
  const handleReset = () => {
    setInputs(defaultInputs);
  };

  // Get heatmap data when inputs change
  useEffect(() => {
    generateHeatmapData();
  }, [inputs]);

  // Generate heatmap data
  const generateHeatmapData = async () => {
    try {
      const data = await blackScholesApi.generateHeatmap(inputs);
      setHeatmapData(data);
    } catch (error) {
      console.error('Error generating heatmap data:', error);
    }
  };

  return (
    <Container theme={theme}>
      <Sidebar theme={theme}>
        <Title theme={theme}>Input Parameters</Title>
        <InputForm
          inputs={inputs}
          onInputChange={handleInputChange}
          onReset={handleReset}
          isLoading={isLoading}
        />
      </Sidebar>

      <MainContent>
        <ResultsGrid>
          <ResultsDisplay
            results={results}
            greeks={greeks}
            isLoading={isLoading}
          />
        </ResultsGrid>

        <HeatmapDisplay
          heatmapData={heatmapData}
          isLoading={isLoading}
        />
      </MainContent>
    </Container>
  );
};

export default BlackScholesCalculator;
