import React from 'react';
import { Routes, Route, Link } from 'react-router-dom';
import styled from 'styled-components';
import { useTheme } from './context/ThemeContext';
import OptionsWatchlist from './components/options-watchlist/OptionsWatchlist';
import BlackScholesCalculator from './components/black-scholes/BlackScholesCalculator';
import './App.css';

const Nav = styled.nav`
  background-color: ${props => props.theme.mode === 'dark' ? '#1a1a2e' : '#2a5298'};
  color: white;
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;
  align-items: center;
`;

const StyledLink = styled(Link)`
  color: white;
  text-decoration: none;
  font-weight: bold;
  font-size: 1.1rem;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  transition: background-color 0.3s;

  &:hover {
    background-color: ${props => props.theme.mode === 'dark' ? '#2e2e4e' : '#3a62a7'};
  }

  &.active {
    background-color: ${props => props.theme.mode === 'dark' ? '#4a4a6a' : '#4a72b7'};
  }
`;

const MainContent = styled.main`
  padding: 2rem;
  min-height: calc(100vh - 60px);
  background-color: ${props => props.theme.mode === 'dark' ? '#121212' : '#f5f5f5'};
  color: ${props => props.theme.mode === 'dark' ? '#fff' : '#333'};
`;

const ThemeToggleButton = styled.button`
  background-color: transparent;
  border: 2px solid white;
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  cursor: pointer;
  font-weight: bold;
  transition: all 0.3s;

  &:hover {
    background-color: white;
    color: ${props => props.theme.mode === 'dark' ? '#1a1a2e' : '#2a5298'};
  }
`;

function App() {
  const { theme, toggleTheme } = useTheme();

  return (
    <div className="App">
      <Nav theme={theme}>
        <h1>Financial Tools</h1>
        <NavLinks>
          <StyledLink theme={theme} to="/">Options Watchlist</StyledLink>
          <StyledLink theme={theme} to="/black-scholes">Black-Scholes Calculator</StyledLink>
          <ThemeToggleButton theme={theme} onClick={toggleTheme}>
            {theme.mode === 'dark' ? 'ðŸŒž Light' : 'ðŸŒ™ Dark'}
          </ThemeToggleButton>
        </NavLinks>
      </Nav>
      <MainContent theme={theme}>
        <Routes>
          <Route path="/" element={<OptionsWatchlist />} />
          <Route path="/black-scholes" element={<BlackScholesCalculator />} />
        </Routes>
      </MainContent>
    </div>
  );
}

export default App;
