import React from 'react';
import styled from 'styled-components';
import { useTheme } from '../../context/ThemeContext';

const HeatmapContainer = styled.div`
  background-color: ${props => props.theme.mode === 'dark' ? '#2d2d2d' : 'white'};
  border-radius: 8px;
  box-shadow: 0 2px 10px rgba(0, 0, 0, ${props => props.theme.mode === 'dark' ? '0.5' : '0.1'});
  padding: 1.5rem;
  margin-top: 1.5rem;
  border: 1px solid ${props => props.theme.mode === 'dark' ? '#404040' : 'transparent'};
`;

const HeatmapTitle = styled.h2`
  font-size: 1.25rem;
  margin-bottom: 1rem;
  color: ${props => props.theme.mode === 'dark' ? '#e0e0e0' : '#333'};
`;

const HeatmapsGrid = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1.5rem;

  @media (max-width: 992px) {
    grid-template-columns: 1fr;
  }
`;

const HeatmapWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 1rem;
`;

const HeatmapGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(${props => props.cols}, 1fr);
  gap: 1px;
  background-color: ${props => props.theme.mode === 'dark' ? '#404040' : '#ddd'};
  padding: 1px;
  width: 100%;
  max-width: 600px;
`;

const Cell = styled.div`
  aspect-ratio: 1;
  background-color: ${props => props.color};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.75rem;
  color: ${props => props.textColor};
  transition: all 0.2s;
  cursor: pointer;

  &:hover {
    transform: scale(1.1);
    z-index: 1;
  }
`;

const AxisLabel = styled.div`
  font-size: 0.8rem;
  color: ${props => props.theme.mode === 'dark' ? '#b0b0b0' : '#666'};
  margin: ${props => props.margin};
  text-align: center;
  min-width: 3rem;
`;

const Legend = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 1rem;
  gap: 0.5rem;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.25rem;
  font-size: 0.8rem;
`;

const LegendColor = styled.div`
  width: 20px;
  height: 10px;
  background-color: ${props => props.color};
`;

const LoadingMessage = styled.div`
  text-align: center;
  padding: 2rem;
  color: ${props => props.theme.mode === 'dark' ? '#b0b0b0' : '#666'};
  font-style: italic;
`;

const getColorForValue = (value, min, max) => {
  const ratio = (value - min) / (max - min);
  const hue = ((1 - ratio) * 240).toFixed(0); // 240 is blue, 0 is red
  return `hsl(${hue}, 70%, 50%)`;
};

const getTextColor = (backgroundColor) => {
  const hue = parseInt(backgroundColor.match(/hsl\((\d+)/)[1]);
  return hue > 120 ? '#000' : '#fff';
};

const HeatmapDisplay = ({ heatmapData, isLoading }) => {
  const { theme } = useTheme();
  if (isLoading) {
    return (
      <HeatmapContainer theme={theme}>
        <HeatmapTitle theme={theme}>Price Heatmaps</HeatmapTitle>
        <LoadingMessage theme={theme}>Generating heatmaps...</LoadingMessage>
      </HeatmapContainer>
    );
  }

  if (!heatmapData) {
    return null;
  }

  const renderHeatmap = (data, title) => {
    const flatData = data.flat();
    const min = Math.min(...flatData);
    const max = Math.max(...flatData);

    return (
      <HeatmapWrapper>
        <h3>{title}</h3>
        <AxisLabel margin="0 0 0.5rem 0" theme={theme}>
          Volatility (Ïƒ)
        </AxisLabel>
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
            {heatmapData.sigma_range.map((sigma, i) => (
              <AxisLabel key={i} margin="0 0.5rem" theme={theme}>
                {sigma.toFixed(2)}
              </AxisLabel>
            )).reverse()}
          </div>
          <HeatmapGrid cols={heatmapData.S_range.length} theme={theme}>
            {data.map((row, i) => 
              row.map((value, j) => {
                const color = getColorForValue(value, min, max);
                const textColor = getTextColor(color);
                return (
                  <Cell 
                    key={`${i}-${j}`} 
                    color={color}
                    textColor={textColor}
                    title={`Stock Price: $${heatmapData.S_range[j].toFixed(2)}\nVolatility: ${heatmapData.sigma_range[i].toFixed(2)}\nPrice: $${value.toFixed(2)}`}
                  >
                    {value.toFixed(1)}
                  </Cell>
                );
              })
            ).reverse()}
          </HeatmapGrid>
        </div>
        <AxisLabel margin="0.5rem 0 0 0" theme={theme}>
          Stock Price ($)
        </AxisLabel>
        <div style={{ display: 'flex', justifyContent: 'space-between', width: '100%', maxWidth: '600px', padding: '0 1rem' }}>
          {heatmapData.S_range.map((price, i) => (
            <AxisLabel key={i} theme={theme}>
              {price.toFixed(0)}
            </AxisLabel>
          ))}
        </div>
        <Legend>
          <LegendItem>
            <LegendColor color="hsl(240, 70%, 50%)" />
            <span>${min.toFixed(2)}</span>
          </LegendItem>
          <LegendItem>
            <LegendColor color="hsl(120, 70%, 50%)" />
            <span>${((max + min) / 2).toFixed(2)}</span>
          </LegendItem>
          <LegendItem>
            <LegendColor color="hsl(0, 70%, 50%)" />
            <span>${max.toFixed(2)}</span>
          </LegendItem>
        </Legend>
      </HeatmapWrapper>
    );
  };

  return (
    <HeatmapContainer theme={theme}>
      <HeatmapTitle theme={theme}>Price Heatmaps</HeatmapTitle>
      <HeatmapsGrid>
        {renderHeatmap(heatmapData.call_heatmap, 'Call Option Prices')}
        {renderHeatmap(heatmapData.put_heatmap, 'Put Option Prices')}
      </HeatmapsGrid>
    </HeatmapContainer>
  );
};

export default HeatmapDisplay;
