import React, { useState, useEffect } from 'react';
import HeatmapDisplay from './HeatmapDisplay';
import styled from 'styled-components';
import { blackScholesApi } from '../../services/api';
import InputForm from './InputForm';
import ResultsDisplay from './ResultsDisplay';

const Container = styled.div`
  display: flex;
  min-height: calc(100vh - 60px);
`;

const Sidebar = styled.div`
  width: 300px;
  background-color: #f8f9fa;
  padding: 1.5rem;
  border-right: 1px solid #e9ecef;
  height: 100%;
  overflow-y: auto;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 1.5rem;
  overflow-y: auto;
`;

const Title = styled.h1`
  margin-bottom: 1.5rem;
  color: #333;
  font-size: 1.5rem;
`;

const ResultsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1rem;
`;

const BlackScholesCalculator = () => {
  // Default input values
  const defaultInputs = {
    stock_price: 100,
    strike_price: 100,
    time_to_expiry: 30,
    risk_free_rate: 0.05,
    volatility: 0.2,
    time_in_days: true
  };

  // State management
  const [inputs, setInputs] = useState(defaultInputs);
  const [results, setResults] = useState(null);
  const [greeks, setGreeks] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [heatmapData, setHeatmapData] = useState(null);

  // Calculate option price when inputs change
  useEffect(() => {
    calculateOptionPrice();
  }, [inputs]);

  // Calculate option price
  const calculateOptionPrice = async () => {
    try {
      setIsLoading(true);

      // Calculate for both call and put
      const callResponse = await blackScholesApi.calculateOptionPrice({ ...inputs, option_type: 'call' });
      const putResponse = await blackScholesApi.calculateOptionPrice({ ...inputs, option_type: 'put' });

      // Get Greeks for both
      const callGreeks = await blackScholesApi.calculateGreeks({ ...inputs, option_type: 'call' });
      const putGreeks = await blackScholesApi.calculateGreeks({ ...inputs, option_type: 'put' });

      setResults({
        call_price: callResponse.call_price,
        put_price: putResponse.put_price
      });

      setGreeks({
        call: callGreeks,
        put: putGreeks
      });
    } catch (error) {
      console.error('Error in Black-Scholes calculation:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Handle input changes
  const handleInputChange = (name, value) => {
    setInputs({
      ...inputs,
      [name]: value
    });
  };



  // Reset inputs to defaults
  const handleReset = () => {
    setInputs(defaultInputs);
  };

  // Get heatmap data when inputs change
  useEffect(() => {
    generateHeatmapData();
  }, [inputs]);

  // Generate heatmap data
  const generateHeatmapData = async () => {
    try {
      const data = await blackScholesApi.generateHeatmap(inputs);
      setHeatmapData(data);
    } catch (error) {
      console.error('Error generating heatmap data:', error);
    }
  };

  return (
    <Container>
      <Sidebar>
        <Title>Input Parameters</Title>
        <InputForm
          inputs={inputs}
          onInputChange={handleInputChange}
          onReset={handleReset}
          isLoading={isLoading}
        />
      </Sidebar>

      <MainContent>
        <ResultsGrid>
          <ResultsDisplay
            results={results}
            greeks={greeks}
            isLoading={isLoading}
          />
        </ResultsGrid>

        <HeatmapDisplay
          heatmapData={heatmapData}
          isLoading={isLoading}
        />
      </MainContent>
    </Container>
  );
};

export default BlackScholesCalculator;
